    @click.stop="props.data.opended = !props.data.opended"
  >
    <i
      :class="{
        'tree-anchor': props.data?.children,
        'tree-icon': props.data?.children?.length,
      }"
      role="presentation"
    ></i>

    <div
      :class="{
        'tree-anchor': true,
        'tree-selected': props.data?.selected,
        'tree-hovered': props.data?.isHover,
      }"
    >
      <i
        class="tree-icon"
        :class="{
          'no-filder': !props.data?.children?.length,
        }"
        role="presentation"
      ></i>
      <span>{{ data.text }}</span>
    </div>

    <ul role="group" ref="group" class="tree-children" v-if="isFolder">
      <tree-item
        v-for="(child, index) in props.data?.children"
        :key="index"
        :data="child"
        @iconClick="props.data.opended = !props.data.opended"
      >
      </tree-item>
    </ul>
  </li>
